import React,{ useState, useEffect }  from "react";
import { dbService } from "fbase";

const Home = ({userObj}) => {
    const [nweet, setNweet] = useState("");
    const [nweets, setNweets] = useState([]);
    /*
    const getNweets = async () => {
      // firebase database에서 "nweets"라는 collection를 가지고 온다. get()이라는 함수를 사용 
      // https://firebase.google.com/docs/firestore/query-data/get-data
      const dbNweets = await dbService.collection("nweets").get(); 
      dbNweets.forEach((document) => {
        // nweetObject를 만들어서 
        const nweetObject = {
          ...document.data(),
          id: document.id,
        };
        setNweets( (prev) => [nweetObject, ...prev] );
      });
    };
    */
    useEffect(() => {
        dbService.collection("nweets").onSnapshot((snapshot) => {
            const nweetArray = snapshot.docs.map
            ( 
                (doc) => 
                    ({
                        id: doc.id,
                        ...doc.data(),
                    })
            );
            console.log ( nweetArray)
            setNweets(nweetArray);
            });
    }, []);
    /*
    데이터베이스에서 뭔가를 하게 되면 바로 알 수 있도록 해주는 것. 
     https://firebase.google.com/docs/reference/node/firebase.firestore.CollectionReference

    snapshot는 document를 가지고 있고 queary(array사용)문이다. 
    모든 doc는 objects를 반환 한다.  (doc) => ( { id: doc.id, ...doc.data(),} )

    */

    const onSubmit = async (event) => {
        event.preventDefault();
        await dbService.collection("nweets").add({
            text: nweet,
            createdAt: Date.now(),
            creatorId: userObj.uid            
          });
        setNweet("");
    };

    //console.log(nweets);

    const onChange = (event) => {
      const {
        target: { value },
      } = event;
      setNweet(value);
    };
    return (
      <div>
        <form onSubmit={onSubmit}>
          <input
            value={nweet}
            onChange={onChange}
            type="text"
            placeholder="What's on your mind?"
            maxLength={120}/>
          <input type="submit" value="Nweet" />
        </form>
        <div>
            {nweets.map((nweet) => (
            <div key={nweet.id}>
                <h4>{nweet.nweet}</h4>
                <h4>{nweet.text}</h4>
            </div>
        ))}
      </div>
      </div>
    );
  };
  export default Home;

