import React,{ useState, useEffect }  from "react";
import { v4 as uuidv4 } from "uuid";      // image's unique identifier 
import { dbService, storageService } from "fbase";
import Nweet from "components/Nweet";

const Home = ({userObj}) => {
    const [nweet, setNweet] = useState("");
    const [nweets, setNweets] = useState([]);
    const [attachment, setAttachment] = useState(); // image 

    /*
      const getNweets = async () => {
        // firebase database에서 "nweets"라는 collection를 가지고 온다. get()이라는 함수를 사용 
        // https://firebase.google.com/docs/firestore/query-data/get-data
        const dbNweets = await dbService.collection("nweets").get(); 
        dbNweets.forEach((document) => {
          // nweetObject를 만들어서 
          const nweetObject = {
            ...document.data(),
            id: document.id,
          };
          setNweets( (prev) => [nweetObject, ...prev] );
        });
      };
    */

    useEffect(() => {
        dbService.collection("nweets").onSnapshot((snapshot) => {
            const nweetArray = snapshot.docs.map
            ( 
                (doc) => 
                    ({
                        id: doc.id,
                        ...doc.data(),
                    })
            );
            console.log ( nweetArray)
            setNweets(nweetArray);
            });
    }, []);

    const onSubmit = async (event) => {
        event.preventDefault();
        const fileRef = storageService.ref().child(`${userObj.uid}/${uuidv4()}`);
        const response = await fileRef.putString(attachment, "data_url");
        const attachmentUrl = await response.ref.getDownloadURL();
        const nweet = {
          text : nweet,
          createdAt: Date.now(),
          creatorId: userObj.uid   ,
          attachmentUrl
        }// nweet obj 
        await dbService.collection("nweets").add(nweet);
        setNweet("");
        setAttachment("");
    };

    //console.log(nweets);

    const onChange = (event) => {
      const {
        target: { value },
      } = event;
      setNweet(value);
    };

    const onFileChange = (event) => {
      const {
        target: { files },
      } = event;

      // evnet 안에서 target 안으로 가서 파일을 받아 온다. 
      const theFile = files[0];                 //In the  "file", just bring the first one. 
      const reader = new FileReader();          // fileReader API, 사용자가 올린 이미지(파일)을 읽기 위한 변수
      reader.onloadend = (finishedEvent) => {   // reader에 event listener를 추가
        const {
          currentTarget: { result },
        } = finishedEvent;
        setAttachment(result);
      };
      reader.readAsDataURL(theFile); // 최종적으로 데이터를 가지고 올수 있음 
    };
    const onClearAttachment = () => setAttachment(null);

    return (
      <div>
        <form onSubmit={onSubmit}>
          <input
            value={nweet}
            onChange={onChange}
            type="text"
            placeholder="What's on your mind?"
            maxLength={120}/>
          <input type ="file" accept = "image/*" onChange = {onFileChange}/>
          <input type="submit" value="Nweet" />
          {attachment && (
          <div>
            <img src={attachment} width="300px" height="200px" />
            <button onClick={onClearAttachment}>Clear</button>
          </div>
        )}
        </form>
        <div>
            {nweets.map((nweet) => (
            < Nweet 
                key ={nweet.id} 
                nweetObj={nweet} 
                isOwner={nweet.creatorId === userObj.uid} 
            />
        ))}
      </div>
      </div>
    );
  };
  export default Home;
